import React from "react";

class App extends React.Component{
  state = {
    count: 0
  };
  render() { // react 는 자동으로 이 render method 를 실행하려 함
    return <div>
      <h1>The number is : {this.state.count}</h1>
      <button>Add</button>
      <button>Minus</button>
    </div>
  }
}

export default App;
